global_text = "team,open_play_shots,open_play_xg,open_play_goals,set_play_shots,set_play_xg,set_play_goals,minutes\n"
def parse_table(html_table, league_name, minutes, exceptions={}):
    global global_text
    soup = BeautifulSoup(html_table, 'html.parser')
    table = soup.select('table[class="data-table"]')[0]
    even_rows = table.select('tr[class="even"]')
    odd_rows = table.select('tr[class="odd"]')
    rows = even_rows + odd_rows
    league_text = "team,open_play_shots,open_play_xg,open_play_goals,set_play_shots,set_play_xg,set_play_goals,minutes\n"
    for row in rows:
        # print(row)
        team_name = row.select('span[class="team-name"]')[0].text
        open_play_shots = row.select('td[class$="team-xg-for-shots"]')[0].text
        open_play_xg = row.select('td[class$="team-xg-for-xG"]')[0].text
        open_play_goals = row.select('td[class$="team-xg-for-goals"]')[0].text
        set_play_shots = row.select('td[class$="team-xg-for-sp shots"]')[0].text
        set_play_xg = row.select('td[class$="team-xg-for-sp xG"]')[0].text
        set_play_goals = row.select('td[class$="team-xg-for-sp goals"]')[0].text
        team_minutes = exceptions[team_name] if team_name in exceptions.keys() else minutes
        row_text = f"{team_name},{open_play_shots},{open_play_xg},{open_play_goals},{set_play_shots},{set_play_xg},{set_play_goals},{team_minutes}\n"
        league_text += row_text
        global_text += row_text
    with open(f"{data_dir}/{league_name}.csv", "w") as f:
        f.write(league_text)


